import user_interface
import game_logic
import data_management

def main():
    user_interface.display_welcome()
    balance = data_management.initialize_balance()

    while True:
        bet = user_interface.get_bet(balance)
        deck = game_logic.create_deck()
        game_logic.shuffle_deck(deck)

        player_hand = game_logic.deal_hand(deck)
        dealer_hand = game_logic.deal_hand(deck)

        user_interface.display_hand(player_hand, dealer_hand, hidden=True)

        game_logic.dealer_turn(dealer_hand, deck)
        result = game_logic.check_winner(player_hand, dealer_hand)

        balance = data_management.update_balance(balance, bet, result)
        user_interface.display_result(result, balance)

        if not user_interface.ask_continue():
            break

if __name__ == "__main__":
    main()
```

