def display_welcome():
    print("Welcome to Command Line Blackjack!")
    print("Instructions: Place your bet, get dealt a hand, and try to beat the dealer.")

def get_bet(balance):
    while True:
        try:
            bet = int(input(f"Your balance is {balance}. Place your bet: "))
            if 0 < bet <= balance:
                return bet
            else:
                print("Invalid bet. Please enter a value within your balance.")
        except ValueError:
            print("Invalid input. Please enter a number.")

def display_hand(player_hand, dealer_hand, hidden=True):
    print("\nPlayer hand:", player_hand)
    if hidden:
        print("Dealer hand: [hidden]", dealer_hand[1:])
    else:
        print("Dealer hand:", dealer_hand)

def display_result(result, balance):
    if result == "win":
        print("You win!")
    elif result == "lose":
        print("You lose.")
    else:
        print("It's a tie.")
    print(f"Your new balance is {balance}.")

def ask_continue():
    while True:
        choice = input("Do you want to continue playing? (y/n): ").lower()
        if choice == "y":
            return True
        elif choice == "n":
            return False
        else:
            print("Invalid input. Please enter 'y' or 'n'.")
