                if (data) {
                    Object.keys(data).forEach(k => {
                        if (typeof data[k] == "string") props[k] = data[k];
                        else if (typeof data[k] == "number") measures[k] = data[k];
                        else props[k] = JSON.stringify(data[k] || '');
                    });
                } else {
                    data = {};
