import random
import string

def load_dictionary():
    with open("dictionary.txt", "r") as f:
        words = f.read().splitlines()
    return words

def is_legal_word(word, dictionary):
    return word in dictionary

def generate_hidden_word(dictionary):
    return random.choice(dictionary)

def get_feedback(user_word, hidden_word):
    feedback = ""
    for i, letter in enumerate(user_word):
        if letter in hidden_word:
            if user_word[i] == hidden_word[i]:
                feedback += f"{letter} is in the correct position ({i + 1}). "
            else:
                feedback += f"{letter} is in the hidden word but not in the correct position. "
    return feedback

def main():
    dictionary = load_dictionary()
    hidden_word = generate_hidden_word(dictionary)
    attempts = 6
    while attempts > 0:
        user_word = input("Enter a 5-letter word: ").lower()
        if not is_legal_word(user_word, dictionary):
            print("Illegal word. Please enter a legal 5-letter word.")
            continue
        feedback = get_feedback(user_word, hidden_word)
        print(feedback)
        if user_word == hidden_word:
            print("Congratulations! You guessed the hidden word.")
            break
        attempts -= 1
    print(f"The hidden word was: {hidden_word}")

if __name__ == "__main__":
    main()
